import * as React from 'react';
import { Button, View, Text, StyleSheet, TextInput, TouchableOpacity, ScrollView} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

// Login screen
function LoginScreen({ navigation }) {
  return (
    <View style={styles.view}>
      <h1>Welcome!</h1>
      <TextInput style={{height: 40, width: 250, margin: 12, borderWidth: 1, padding: 10,}} 
      placeholder="Username" keyboardType="text"/>
      <TextInput style={{height: 40, width: 250, margin: 12, borderWidth: 1, padding: 10,}}  
      placeholder="Password" keyboardType="text"/>

      <TouchableOpacity style={{alignItems: "center", padding: 10, marginLeft:150}} 
      onPress={() => navigation.navigate('Reset Password')}>
        <Text>forgot password</Text>
      </TouchableOpacity>

      <Button title="Register" onPress={() => navigation.navigate('Dashboard')}/>
    </View>
  );
}
// Register screen
function RegisterScreen({ navigation }) {
  return (
    <View style={styles.view}>
      <TextInput style={{height: 40, width: 250, margin: 12, borderWidth: 1, padding: 10,}} 
      placeholder="Enter e-mail" keyboardType="text"/>
      <TextInput style={{height: 40, width: 250, margin: 12, borderWidth: 1, padding: 10,}}  
      placeholder="Enter Password" keyboardType="text"/>

      <TouchableOpacity style={{alignItems: "center", padding: 10, marginLeft:150}} 
      onPress={() => navigation.navigate('Reset Password')}>
        <Text>forgot password</Text>
      </TouchableOpacity>

      <Button title="Register" onPress={() => navigation.navigate('Dashboard')}/>
    </View>
  );
}

// Dashboard screen
function DashboardScreen({ navigation }) {
  return (
    <View style={{padding:5,flex: 1, alignItems: 'center', backgroundColor:'white'}}>
          <TouchableOpacity style={{width:"100%", height:80, backgroundColor:'gray', marginTop:30}}
          onPress={()=>navigation.navigate('Fuel Types')}>
            <Text style={{textAlign:'center', fontWeight:'bold',fontSize:30,color:'white',padding:20}}>Fuel</Text>
          </TouchableOpacity>

          <TouchableOpacity style={{width:"100%", height:80, backgroundColor:'gray', marginTop:30}}
          onPress={()=>navigation.navigate('Live Queue Map')}>
            <Text style={{textAlign:'center', fontWeight:'bold',fontSize:30,color:'white',padding:20}}>Live Queue Map</Text>
          </TouchableOpacity>

          <TouchableOpacity style={{width:"100%", height:80, backgroundColor:'gray', marginTop:30}}
          onPress={()=>navigation.navigate('Notifications')}>
            <Text style={{textAlign:'center', fontWeight:'bold',fontSize:30,color:'white',padding:20}}>Notifications</Text>
          </TouchableOpacity>
      </View>
  );
}

// rest password screen
function ResetScreen({ navigation }) {
  return (
    <View style={styles.view}>
      <TextInput style={{height: 40, width: 250, margin: 12, borderWidth: 1, padding: 10,}} 
       placeholder="Enter Name" keyboardType="text"/>
      <TextInput style={{height: 40, width: 250, margin: 12, borderWidth: 1, padding: 10,}} 
       placeholder="Enter City" keyboardType="text"/>
      <TextInput style={{height: 40, width: 250, margin: 12, borderWidth: 1, padding: 10,}} 
       placeholder="Enter Phone Number" keyboardType="text"/>
      <TextInput style={{height: 40, width: 250, margin: 12, borderWidth: 1, padding: 10,}} 
       placeholder="Enter e-mail" keyboardType="text"/>
      <Button title="Next" onPress={() => navigation.navigate('Login')}/>
    </View>
  );
}
// Fuel Types screen
function FuelTypes({ navigation }) {
  return (
    <View style={{padding:5,flex: 1, alignItems: 'center', backgroundColor:'white'}}>
          <TouchableOpacity style={{width:"100%", height:80, backgroundColor:'gray', marginTop:30}}
          onPress={()=>navigation.navigate('Petrol')}>
            <Text style={{textAlign:'center', fontWeight:'bold',fontSize:30,color:'white',padding:20}}>Petrol</Text>
          </TouchableOpacity>

          <TouchableOpacity style={{width:"100%", height:80, backgroundColor:'gray', marginTop:30}}
          onPress={()=>navigation.navigate('Diesel')}>
            <Text style={{textAlign:'center', fontWeight:'bold',fontSize:30,color:'white',padding:20}}>Diesel</Text>
          </TouchableOpacity>

          <TouchableOpacity style={{width:"100%", height:80, backgroundColor:'gray', marginTop:30}}>
            <Text style={{textAlign:'center', fontWeight:'bold',fontSize:30,color:'white',padding:20}}>Kerosine</Text>
          </TouchableOpacity>

          <TouchableOpacity style={{width:"100%", height:80, backgroundColor:'gray', marginTop:30}}
          onPress={()=>navigation.navigate('LP Gas')}>
            <Text style={{textAlign:'center', fontWeight:'bold',fontSize:30,color:'white',padding:20}}>LP Gas</Text>
          </TouchableOpacity>
      </View>
  );  
}

// petrol details
function PetrolDetails() {
  return (

    <View style={styles.view}>
      <h1>Enter details</h1>
      <TextInput 
        style={{borderColor:'black', fontSize:15, borderWidth:1, borderRadius:10, padding:10, marginTop:20}}
        placeholder="Petrol Type"
      />

      <TextInput 
        style={{borderColor:'black', fontSize:15, borderWidth:1, borderRadius:10, padding:10, marginTop:20}}
        placeholder="District"
      />

      <TextInput 
        style={{borderColor:'black', fontSize:15, borderWidth:1, borderRadius:10, padding:10, marginTop:20,marginBottom:20}}
        placeholder="City or Town"
      />
      <Button title="Search"/>
    </View>

  );
}
// Diesel details
function DieselDetails() {
  return (

    <View style={styles.view}>
      <h1>Enter details</h1>
      <TextInput 
        style={{borderColor:'black', fontSize:15, borderWidth:1, borderRadius:10, padding:10, marginTop:20}}
        placeholder="Diesel Type"
      />

      <TextInput 
        style={{borderColor:'black', fontSize:15, borderWidth:1, borderRadius:10, padding:10, marginTop:20}}
        placeholder="District"
      />

      <TextInput 
        style={{borderColor:'black', fontSize:15, borderWidth:1, borderRadius:10, padding:10, marginTop:20,marginBottom:20}}
        placeholder="City or Town"
      />
      <Button title="Search"/>
    </View>

  );
}
// LP Gas details
function LPGasDetails() {
  return (

    <View style={styles.view}>
      <h1>Enter details</h1>
      <TextInput 
        style={{borderColor:'black', fontSize:15, borderWidth:1, borderRadius:10, padding:10, marginTop:20}}
        placeholder="LP Gas Type"
      />

      <TextInput 
        style={{borderColor:'black', fontSize:15, borderWidth:1, borderRadius:10, padding:10, marginTop:20}}
        placeholder="District"
      />

      <TextInput 
        style={{borderColor:'black', fontSize:15, borderWidth:1, borderRadius:10, padding:10, marginTop:20,marginBottom:20}}
        placeholder="City or Town"
      />
      <Button title="Search"/>
    </View>

  );
}

// Live Queue Map screen
function MapScreen() {
  return (
    <View style={styles.view}>
      <h2>Live Queue Map screen</h2>
    </View>
  );
}
// Notifications screen
function NotificationScreen() {
  return (
    <View style={styles.view}>
      <h2>Notifications screen</h2>
    </View>
  );
}


const Stack = createNativeStackNavigator();

// main function
function App() {
  return (
    // navigator
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Login">
        <Stack.Screen name="Login" component={LoginScreen} />
        <Stack.Screen name="Register" component={RegisterScreen} />
        <Stack.Screen name="Dashboard" component={DashboardScreen} />
        <Stack.Screen name="Reset Password" component={ResetScreen} />
        <Stack.Screen name="Fuel Types" component={FuelTypes} />
        <Stack.Screen name="Live Queue Map" component={MapScreen} />
        <Stack.Screen name="Notifications" component={NotificationScreen} />
        <Stack.Screen name="Petrol" component={PetrolDetails} />
        <Stack.Screen name="Diesel" component={DieselDetails} />
        <Stack.Screen name="LP Gas" component={LPGasDetails} />
      </Stack.Navigator>
    </NavigationContainer>


  );
}

// styles
const styles = StyleSheet.create({
  view: {
    flex: 1, 
    alignItems: 'center', 
    justifyContent: 'center',
    padding: 5,
  },
});

export default App;
