import * as React from 'react';
import { Button, View, Text, StyleSheet, TextInput, TouchableOpacity} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

// loggin screen
function RegisterScreen({ navigation }) {
  return (
    <View style={styles.view}>
      <TextInput style={styles.inputReg} placeholder="Enter e-mail" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter Password" keyboardType="text"/>

      <TouchableOpacity style={styles.forgotButton} onPress={() => navigation.navigate('Reset Password')}>
        <Text>forgot password</Text>
      </TouchableOpacity>

      <Button title="Register" onPress={() => navigation.navigate('Dashboard')}/>
    </View>
  );
}

// dashboard screen
function DashboardScreen({ navigation }) {
  return (
    <View style={styles.dashBoard}>
      <TouchableOpacity style={styles.dashBoardTiles} onPress={() => navigation.navigate('Fuel Types')}>
        <Text style={styles.dashboardtext}>Fuel</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.dashBoardTiles} onPress={() => navigation.navigate('Live Queue Map')}>
        <Text style={styles.dashboardtext}>Live Queue Map</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.dashBoardTiles} onPress={() => navigation.navigate('Notifications')}>
        <Text style={styles.dashboardtext}>Notifications</Text>
      </TouchableOpacity>
    </View>
  );
}

// rest password screen
function ResetScreen() {
  return (
    <View style={styles.view}>
      <TextInput style={styles.inputReg} placeholder="Enter Name" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter City" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter Phone Number" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter e-mail" keyboardType="text"/>
    </View>
  );
}
// Fuel Types screen
function FuelScreen({ navigation }) {
  return (
    <View style={styles.dashBoard}>
      <TouchableOpacity style={styles.dashBoardTiles} onPress={() => navigation.navigate('Petrol')}>
        <Text style={styles.dashboardtext}>Petrol</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.dashBoardTiles} onPress={() => navigation.navigate('Diesel')}>
        <Text style={styles.dashboardtext}>Diesel</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.dashBoardTiles} onPress={() => navigation.navigate('Kerosine')}>
        <Text style={styles.dashboardtext}>Kerosine</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.dashBoardTiles} onPress={() => navigation.navigate('LP Gas')}>
        <Text style={styles.dashboardtext}>LP Gas</Text>
      </TouchableOpacity>
    </View>
  );  
}

// petrol
function PetrolScreen() {
  return (
    <View style={styles.view}>
      <Text style={styles.fuelText}>
        <h1>Petroleum</h1>
         Petroleum, also known as crude oil, or simply oil, is a naturally occurring, yellowish-black liquid found in geological formations. It is commonly refined into various fuels and chemicals. Components of petroleum are separated by means of distillation. Petroleum mainly consists of hydrocarbons as well as traces of other organic compounds.[1] The name petroleum covers both naturally occurring unprocessed crude oil and petroleum products that are made up of refined crude oil. A fossil fuel, petroleum is formed when large quantities of dead organisms, mostly zooplankton and algae, are buried underneath sedimentary rock and subjected to both prolonged heat and pressure.
      </Text>
    </View>
  );
}
// Diesel
function DieselScreen() {
  return (
    <View style={styles.view}>
      <Text style={styles.fuelText}>
        <h1>Diesel fuel</h1>
            "Diesel oil" redirects here. Not to be confused with Lubricating oil for diesel engines. A tank of diesel fuel on a truck
            Generally speaking, diesel fuel /ˈdiːzəl/, also called diesel oil, is any liquid fuel specifically designed for use in a diesel engine, a type of internal combustion engine in which fuel ignition takes place without a spark as a result of compression of the inlet air and then injection of fuel. Therefore, diesel fuel needs good compression ignition characteristics.The most common type of diesel fuel is a specific fractional distillate of petroleum fuel oil, but alternatives that are not derived from petroleum, such as biodiesel, biomass to liquid (BTL) or gas to liquid (GTL) diesel are increasingly being developed and adopted. To distinguish these types, petroleum-derived diesel is increasingly called petrodiesel in some academic circles.[1]
      </Text>
    </View>
  );
}
// Kerosine
function KerosineScreen() {
  return (
    <View style={styles.view}>
      <Text style={styles.fuelText}>
        <h1>Kerosene</h1>
        Kerosene, paraffin, or lamp oil is a combustible hydrocarbon liquid which is derived from petroleum. It is widely used as a fuel in aviation as well as households. Its name derives from Greek: κηρός (keros) meaning "wax", and was registered as a trademark by Canadian geologist and inventor Abraham Gesner in 1854 before evolving into a generic trademark. It is sometimes spelled kerosine in scientific and industrial usage.[1] The term kerosene is common in much of Argentina, Australia, Canada, India, New Zealand, Nigeria, and the United States,[2][3] while the term paraffin (or a closely related variant) is used in Chile, eastern Africa, South Africa, Norway, and in the United Kingdom.[4] The term lamp oil, or the equivalent in the local languages, is common in the majority of Asia and the Southeastern United States.[5] Liquid paraffin (called mineral oil in the US) is a more viscous and highly refined product which is used as a laxative. Paraffin wax is a waxy solid extracted from petroleum.
      </Text>
    </View>
  );
}
// LP Gas
function LPGasScreen() {
  return (
    <View style={styles.view}>
      <Text style={styles.fuelText}>
        <h1>Liquefied petroleum gas</h1>
        Liquefied petroleum gas (LPG, LP gas, or condensate) is a fuel gas made of petrol which contains a flammable mixture of hydrocarbon gases, most commonly propane, butane, and propylene. However, the latter two typically compose 5% or less of the mixture. LPG is used as a fuel gas in heating appliances, cooking equipment, and vehicles. It is increasingly used as an aerosol propellant[1] and a refrigerant,[2] replacing chlorofluorocarbons in an effort to reduce damage to the ozone layer. When specifically used as a vehicle fuel, it is often referred to as autogas. Varieties of LPG that are bought and sold include mixes that are mostly propane (C 3H 8), mostly butane (C 4H 10), and, most commonly, mixes including both propane and butane. In the northern hemisphere winter, the mixes contain more propane, while in summer, they contain more butane.[3][4] In the United States, mainly two grades of LPG are sold: commercial propane and HD-5. These specifications are published by the Gas Processors Association (GPA)[5] and the American Society of Testing and Materials.[6] Propane/butane blends are also listed in these specifications.

      </Text>
    </View>
  );
}

// Live Queue Map screen
function MapScreen() {
  return (
    <View style={styles.view}>
      <TextInput style={styles.inputReg} placeholder="Enter Name" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter City" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter Phone Number" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter e-mail" keyboardType="text"/>
    </View>
  );
}
// Notifications screen
function NotificationScreen() {
  return (
    <View style={styles.view}>
      <TextInput style={styles.inputReg} placeholder="Enter Name" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter City" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter Phone Number" keyboardType="text"/>
      <TextInput style={styles.inputReg} placeholder="Enter e-mail" keyboardType="text"/>
    </View>
  );
}


const Stack = createNativeStackNavigator();

// main function
function App() {
  return (
    // navigator
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Register">
        <Stack.Screen name="Register" component={RegisterScreen} />
        <Stack.Screen name="Dashboard" component={DashboardScreen} />
        <Stack.Screen name="Reset Password" component={ResetScreen} />
        <Stack.Screen name="Fuel Types" component={FuelScreen} />
        <Stack.Screen name="Live Queue Map" component={MapScreen} />
        <Stack.Screen name="Notifications" component={NotificationScreen} />
        <Stack.Screen name="Petrol" component={PetrolScreen} />
        <Stack.Screen name="Diesel" component={DieselScreen} />
        <Stack.Screen name="Kerosine" component={KerosineScreen} />
        <Stack.Screen name="LP Gas" component={LPGasScreen} />
      </Stack.Navigator>
    </NavigationContainer>


  );
}

// styles
const styles = StyleSheet.create({
  view: {
    flex: 1, 
    alignItems: 'center', 
    justifyContent: 'center'
  },
  inputReg: {
    height: 40,
    width: 250,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
  forgotButton: {
    alignItems: "center",
    padding: 10,
    marginLeft:150
  },
  dashBoard:{
    flex:1,
    justifyContent:'space-around',
    alignItems:'center',
  },
  dashBoardTiles:{
    padding:20,
    backgroundColor: 'grey',
    alignItems: 'center',
  },
  dashboardtext:{
    color: "white",
    fontSize:30,
  },
  fuelText:{
    padding: 20,
    textAlign: 'center'
  }
});


export default App;
